+"use client";
+
+import React, { useState, useEffect } from "react";
+import {
+  Table,
+  TableBody,
+  TableCell,
+  TableHead,
+  TableHeader,
+  TableRow,
+} from "@/components/ui/table";
+import { Upload, ChevronDown } from "lucide-react";
+import { getVisitLogs } from "@/services/checkincheckout";
+import { Button } from "@/components/ui/button";
+import { useNavigate } from "react-router-dom";
+// import { getVisitLogs } from "ser";
+
+export default function CheckinCheckoutTable() {
+  const [visitors, setVisitors] = useState([]);
+  const [filteredAllVisitors, setFilteredAllVisitors] = useState([]);
+  const [visitorTypeFilter, setVisitorTypeFilter] = useState("all");
+  const [currentPage, setCurrentPage] = useState(1);
+  const entriesPerPage = 6;
+  const [totalEntries, setTotalEntries] = useState(0);
+  const [loading, setLoading] = useState(false);
+  const navigate = useNavigate();
+
+  useEffect(() => {
+    console.log("am back")
+    fetchVisitors();
+  }, [visitorTypeFilter, currentPage]);
+
+  const fetchVisitors = async () => {
+    setLoading(true);
+    try {
+      const data = await getVisitLogs();
+      console.log(data)
+
+      let allData = data;
+
+      if (visitorTypeFilter !== "all") {
+        allData = allData.filter((v) => v.visitor_type === visitorTypeFilter);
+      }
+
+      setFilteredAllVisitors(allData);
+      setTotalEntries(allData.length);
+
+      const start = (currentPage - 1) * entriesPerPage;
+      const paginated = allData.slice(start, start + entriesPerPage);
+      setVisitors(paginated);
+    } catch (error) {
+      console.error("Error fetching visitors:", error);
+      setVisitors([]);
+      setFilteredAllVisitors([]);
+      setTotalEntries(0);
+    } finally {
+      setLoading(false);
+    }
+  };
+
+  const handleFilterChange = (e) => {
+    setVisitorTypeFilter(e.target.value);
+    setCurrentPage(1);
+  };
+
+  const handleView = (visitor) => {
+    navigate("/view", { state: { visitor } });
+  };
+
+  const formatDate = (dateStr) => {
+    if (!dateStr) return "--";
+    const d = new Date(dateStr);
+    return d.toLocaleString("en-KE", {
+      dateStyle: "medium",
+      timeStyle: "short",
+    });
+  };
+
+  return (
+    <div className="p-6 space-y-6">
+      <div className="flex items-center justify-between">
+        <h2 className="text-lg font-semibold">Live Logs</h2>
+        <div className="flex items-center gap-2">
+          <select
+            value={visitorTypeFilter}
+            onChange={handleFilterChange}
+            className="px-3 py-2 border rounded text-sm"
+          >
+            <option value="all">All</option>
+            <option value="Returning">Returning</option>
+            <option value="New">New</option>
+          </select>
+          <Button variant="outline" size="sm">
+            <Upload className="w-4 h-4 mr-2" />
+            Export
+            <ChevronDown className="ml-2 h-4 w-4" />
+          </Button>
+        </div>
+      </div>
+
+      <div className="overflow-x-auto">
+        <Table>
+          <TableHeader>
+            <TableRow>
+              <TableHead>Visitor Name</TableHead>
+              <TableHead>Phone No.</TableHead>
+              <TableHead>Visit Unit</TableHead>
+              
+              <TableHead>Check-n </TableHead>
+              <TableHead>Check-out</TableHead>
+              <TableHead>Duration</TableHead>
+              
+              <TableHead>Action</TableHead>
+            </TableRow>
+          </TableHeader>
+          <TableBody>
+            {loading ? (
+              <TableRow>
+                <TableCell colSpan={10}>Loading...</TableCell>
+              </TableRow>
+            ) : visitors.length > 0 ? (
+              visitors.map((visitor, index) => (
+                <TableRow key={index}>
+                  <TableCell>{visitor.visitor_name}</TableCell>
+                  <TableCell>{visitor.phone_number}</TableCell>
+                  <TableCell>{visitor.unit_number}</TableCell>
+                  <TableCell>{visitor.visitor_type}</TableCell>
+                  <TableCell>{visitor.visit_date}</TableCell>
+                  <TableCell>{formatDate(visitor.check_in_time)}</TableCell>
+                  <TableCell>{formatDate(visitor.check_out_time)}</TableCell>
+                  <TableCell>{visitor.duration}</TableCell>
+                  <TableCell>{visitor.car_no_plate}</TableCell>
+                  <TableCell>
+                    <button
+                      className="text-blue-600 hover:underline"
+                      onClick={() => handleView(visitor)}
+                    >
+                      View
+                    </button>
+                  </TableCell>
+                </TableRow>
+              ))
+            ) : (
+              <TableRow>
+                <TableCell colSpan={10}>No visitors found</TableCell>
+              </TableRow>
+            )}
+          </TableBody>
+        </Table>
+      </div>
+
+      {/* Pagination */}
+      <div className="flex justify-end mt-4 space-x-2">
+        <Button
+          variant="outline"
+          size="sm"
+          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
+          disabled={currentPage === 1}
+        >
+          Previous
+        </Button>
+        <span className="text-sm py-2 px-4">Page {currentPage}</span>
+        <Button
+          variant="outline"
+          size="sm"
+          onClick={() =>
+            setCurrentPage((prev) =>
+              prev < Math.ceil(totalEntries / entriesPerPage)
+                ? prev + 1
+                : prev
+            )
+          }
+          disabled={currentPage >= Math.ceil(totalEntries / entriesPerPage)}
+        >
+          Next
+        </Button>
+      </div>
+    </div>
+  );
+}

