import React from 'react';
import { 
  AlertTriangle, 
  Phone, 
  Shield, 
  Clock
} from 'lucide-react';

export default function EmergencyControlApp() {
  const emergencyButtons = [
    { label: 'S.O.S', color: 'bg-red-500 hover:bg-red-600', textColor: 'text-white' },
    { label: 'Fire\nAlert', color: 'bg-orange-500 hover:bg-orange-600', textColor: 'text-white' },
    { label: 'Security Concern', color: 'bg-yellow-500 hover:bg-yellow-600', textColor: 'text-black' }
  ];

  const emergencyContacts = [
    {
      icon: Shield,
      title: 'West Brook Security guards',
      phones: ['0712345678', '0723456789']
    },
    {
      icon: AlertTriangle,
      title: 'Nairobi County Fire & Rescue Services',
      phones: ['0780000000', '0790000000']
    },
    {
      icon: Phone,
      title: 'Ambulances',
      phones: ['1800-098-765', '1800-123-456']
    }
  ];

  return (
    <div className="p-6">
      {/* Emergency Control Title */}
      <div className="flex items-center mb-8">
        <img src="/emg.svg" className="w-6 h-6 text-red-500 mr-3" />
        <h1 className="text-2xl font-bold">Emergency Control</h1>
      </div>

        {/* Emergency Buttons */}
        <div className="grid grid-cols-3 gap-4 mb-8">
          {emergencyButtons.map((button, index) => (
            <button
              key={index}
              className={`${button.color} ${button.textColor} p-6 rounded-lg font-bold text-lg transition-colors whitespace-pre-line text-center shadow-lg hover:shadow-xl transform hover:scale-105 transition-transform`}
            >
              {button.label}
            </button>
          ))}
        </div>

        {/* Emergency Alert */}
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 mb-6">
          <div className="flex items-center mb-4">
            <AlertTriangle className="w-6 h-6 text-red-500 mr-3" />
            <h2 className="text-lg font-semibold text-red-800">Emergency Alert</h2>
          </div>
          
          <div className="mb-4">
            <div className="font-semibold text-red-800 mb-1">Fire Drill in Block B</div>
            <div className="text-sm text-red-600">2 minutes ago</div>
          </div>
          
          <div className="bg-white rounded-lg p-4 border border-red-200">
            <div className="font-semibold text-red-800 mb-3">Fire Drill in Block B</div>
            <div className="text-red-700 space-y-2">
              <div>• Evacuate via Stairs B to fire assembly point A</div>
              <div className="font-semibold">Note:</div>
              <div>• Electrical fire, don't use water fire extinguisher.</div>
            </div>
          </div>
        </div>

        {/* Evacuation Timer */}
        <div className="bg-orange-100 border border-orange-200 rounded-lg p-4 mb-6 flex items-center justify-center">
          <Clock className="w-6 h-6 text-orange-600 mr-3" />
          <span className="text-orange-800 mr-2">You have</span>
          <span className="text-2xl font-bold text-orange-800 mx-2">0:00</span>
          <span className="text-orange-800">Minutes to Evacuate</span>
        </div>

        {/* Emergency Contacts */}
        <div className="bg-white rounded-lg border border-gray-200 p-6">
          <div className="flex items-center mb-6">
            <Phone className="w-6 h-6 text-blue-500 mr-3" />
            <h2 className="text-lg font-semibold">Emergency Contacts</h2>
          </div>
          
          <div className="space-y-6">
            {emergencyContacts.map((contact, index) => {
              const Icon = contact.icon;
              return (
                <div key={index} className="flex items-start">
                  <div className="flex items-center mr-6 min-w-0 flex-1">
                    <Icon className="w-6 h-6 text-gray-600 mr-3 flex-shrink-0" />
                    <span className="font-medium text-gray-800">{contact.title}</span>
                  </div>
                  
                  <div className="flex space-x-4">
                    {contact.phones.map((phone, phoneIndex) => (
                      <a
                        key={phoneIndex}
                        href={`tel:${phone}`}
                        className="flex items-center px-3 py-2 bg-blue-100 hover:bg-blue-200 rounded-lg transition-colors"
                      >
                        <Phone className="w-4 h-4 text-blue-600 mr-2" />
                        <span className="text-blue-800 font-medium">{phone}</span>
                      </a>
                    ))}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
}
